import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
/**
 * This is a child component for DropDown Component, it renders the box above actual drop down, which shows the selected value of dropdown.   
 * @method Capsule
 * @param {string} text
 * @param {method} onClickCapsule
 * @param {Boolean} showDropdown
 * 
 */
function Capsule({ text, onClickCapsule, showDropdown }) {
  return (
    <div
      className="dropdown-capsule"
      onClick={(event) => {
        event.stopPropagation();
        onClickCapsule();
      }}
    >
      {text}
      {showDropdown ? (
        <img
          src={`${process.env.PUBLIC_URL}/assets/images/chevron-down.svg`}
          alt="chevron-up"
          className="dropdown-capsule-img-down"
        />
      ) : (
        <img
          src={`${process.env.PUBLIC_URL}/assets/images/chevron-down.svg`}
          alt="chevron-down"
          className="dropdown-capsule-img"
        />
      )}
    </div>
  );
}

export default Capsule;

import { React, useState } from "react";
import "./Dropdown.scss";
import Capsule from "../capsule/Capsule";
import useClickOutside from "../../../../hooks/useClickOutside";
/**
 * This function renders general Dropdowns, used in  API type(GET,POST,DELETE,PUT) Dropdown in here.  
 * @method Dropdown
 * @param {string} [inputArray]
 * @param {method} [onSelect]
 * @param {method} [selectedString]
 * 
 */

function Dropdown({ inputArray, onSelect, selectedString }) {
  let [showDropdown, setShowDropdown] = useState(false);
  const containerRef = useClickOutside(() => setShowDropdown(false));

  return (
    <div className="dropdown">
      <Capsule
        text={selectedString}
        onClickCapsule={() => setShowDropdown(!showDropdown)}
        showDropdown={showDropdown}
      ></Capsule>
      {showDropdown && (
        <div className="dropdown-container" ref={containerRef}>
          {inputArray.map((input, index) => (
            <div
              key={index}
              className={`dropdown-container__item ${
                input.selected && `dropdown-selected`
              }`}
              onClick={() => {
                 onSelect(input);
                 setShowDropdown(false);
              }}
            >
              {input.apiType}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Dropdown;

