import {React,useState,useEffect,useRef} from 'react'
/**
 * A custom hook for handle outside click events
 * @method useClickOutside
 */
function useClickOutside(onClickOutside) {


let containerRef = useRef();

useEffect(()=> {

  window.addEventListener('click',(event)=> handleClickOutside(event))
},[])


const handleClickOutside=(event)=> {
  if( containerRef.current && !containerRef.current.contains(event.target)) {

    onClickOutside();
  }
}

return containerRef;

}

export default useClickOutside

import { useState } from "react";
/**
 * A custom hook (replacement for useState) incase the value needs to be stored in the local storage with a key.
 * @method useLocalStorage
 */
const useLocalStorage = (key, initialValue) => {
  const [storedValue, setStoredValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (err) {
      console.error(err);
      return initialValue;
    }
  });

  const setValue = (value) => {
    console.log(value, "value");
    try {
      const valueToStore =
        value instanceof Function ? value(storedValue) : value;
      setStoredValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(storedValue));
    } catch (err) {
      console.error(err);
    }
  };

  return [storedValue, setValue];
};

export default useLocalStorage;

